mapper.select:
SELECT * FROM (SELECT TO_NUMBER(rownum) rnum, A.* FROM (SELECT b.BRD_NO, b.BRD_WRTR, b.BRD_CONTS, b.BRD_ATCH_FILE_ID, b.BRD_RGDT, b.BRD_HIT, b.CH_NO, b.TH_NO, b.BRD_TTL, b.GUBUN, t.TH_TTL, t.TH_CLR, m.MEM_NAME FROM BOARD B LEFT OUTER JOIN CHANNEL_THREAD T ON B.TH_NO = T.TH_NO LEFT OUTER JOIN MEMBER M ON B.BRD_WRTR = M.MEM_NO WHERE B.CH_NO= #{chNo} AND B.TH_NO = 0 AND B.GUBUN='N'
AND B.BRD_TTL LIKE '%' || #{brdSearchWord} || '%'
ORDER BY b.BRD_RGDT DESC) A ) B WHERE 1=1 AND B.RNUM >= #{rowCnt} * (#{page}-1) + 1 AND B.RNUM <= #{rowCnt} * #{page}

mapper.update:
Update board
set
	brd_ttl = #{brdTtl},
	brd_conts = #{brdConts}
where
	brd_no = #{brdNo}
