mapper.select:
SELECT cld_no , cld_file_no , to_char(cld_rgdt , 'YYYY.MM.DD') str_dt , atd.* , to_char(atd.atch_file_regdt, 'YYYY.MM.DD') atch_file_reg_dt_str , m.mem_name FROM cloud cld , atch_file_detail atd, member m where cld.cld_file_no=atd.atch_detail_file_id and cld.mem_no = m.mem_no
and cld.ch_no = #{chNo}
and cld.th_no = #{chMemThNo}
and cld.mem_no = #{memNo} and (cld.ch_no = 0 and cld.th_no = 0)
ORDER BY cld.cld_rgdt desc

mapper.delete:
DELETE FROM cloud
WHERE
    cld_file_no = #{cldFileNo}

    AND mem_no = #{memNo}


    AND ch_no = #{chNo}


    AND th_no = ${thNo}

mapper.update:
UPDATE cloud
SET
   cld_file_no = #{updateFileId},
   cld_rgdt = sysdate,
   mem_no = #{updateMem}
WHERE
	cld_file_no = #{cldFileNo}

    AND mem_no = #{memNo}


    AND ch_no = #{chNo}


    AND th_no = ${thNo}

mapper.insert:

  			select seq_cloud.nextval from dual

  		INSERT INTO cloud (
		    cld_no
		    , mem_no
		    , cld_file_no
		    , ch_no
		    , th_no
		) VALUES (
		    #{cldNo}
		  , #{memNo}
		  , #{cldFileNo}
		  , #{chNo}
		  , #{thNo}
		)
