mapper.insert:

	select seq_eqp_cate.nextval from dual

INSERT INTO equipment_sort (
	eqpmnt_cate_no
	, eqpmnt_cate_nm
	, ch_no
	, eqpmnt_cate_regdate
	, eqpmnt_cate_delyn
) VALUES (
	#{eqpmntCateNo}
	, #{eqpmntCateNm}
	, #{chNo}
	, SYSDATE
	, 'N'
)

mapper.update:
UPDATE
	equipment_sort
SET
	eqpmnt_cate_nm = #{eqpmntCateNm}
WHERE
	eqpmnt_cate_no = #{eqpmntCateNo}

update
	equipment
set
	eqpmnt_nm = #{eqpmntNm}
	, eqpmnt_dt = SYSDATE
	, eqpmnt_due = #{eqpmntDue}
	, eqmnt_content = #{eqmntContent}

		, eqpmnt_atch_file_id = #{eqpAtchFileId}

where
	eqpmnt_no = #{eqpmntNo}

mapper.insert:

	select seq_eqp.nextval from dual

INSERT INTO equipment (
	eqpmnt_no
	, eqpmnt_nm
	, eqpmnt_mngr
	, eqpmnt_using
	, eqpmnt_dt
	, eqpmnt_due
	, eqpmnt_atch_file_id
	, eqmnt_content
	, ch_no
	, th_no
	, eqpmnt_sort_no
	, eqpmnt_delyn
) VALUES (
	#{eqpmntNo}
	, #{eqpmntNm}
	, #{eqpmntMngr}
	, 1
	, SYSDATE
	, #{eqpmntDue}
	, #{eqpAtchFileId}
	, #{eqmntContent}
	, #{chNo}
	, #{thNo}
	, #{eqpmntCateNo}
	, 'N'
)

mapper.select:
SELECT COUNT(*) FROM EQUIPMENT WHERE 1=1 AND CH_NO=#{chNo}
AND TH_NO=#{thNo} OR TH_NO=0
AND EQPMNT_SORT_NO = #{eqpmntCateNo}
AND EQPMNT_NM LIKE '%' || #{eqpSearchWord} || '%'
AND EQPMNT_DELYN='N'

SELECT * FROM (select ROWNUM RNUM, A.* from(SELECT e.eqpmnt_no , e.eqpmnt_nm , e.eqpmnt_mngr , e.eqpmnt_using , to_char(e.eqpmnt_dt , 'yyyy.mm.dd') eqpmnt_dt , e.eqpmnt_due , e.eqpmnt_atch_file_id , e.eqmnt_content , e.ch_no , e.th_no , e.eqpmnt_sort_no eqpmnt_sort_no , eqpst.eqpmnt_cate_nm eqpmntCateNm , chm.ch_mem_nm memNm , e.eqpmnt_delyn , CH.CH_TTL chTtl , CT.TH_TTL thTtl , CT.TH_CLR thClr FROM EQUIPMENT E LEFT OUTER JOIN CHANNEL_MEMBER CHM ON E.EQPMNT_MNGR = CHM.MEM_NO AND E.CH_NO = CHM.CH_NO LEFT OUTER JOIN CHANNEL CH ON E.CH_NO = CH.CH_NO LEFT OUTER JOIN EQUIPMENT_SORT EQPST ON E.EQPMNT_SORT_NO = EQPST.EQPMNT_CATE_NO LEFT OUTER JOIN CHANNEL_THREAD CT ON E.TH_NO=CT.TH_NO WHERE E.CH_NO = #{chNo}
AND (E.TH_NO = #{thNo} OR E.TH_NO = 0)
AND E.EQPMNT_SORT_NO = #{eqpmntCateNo}
AND E.EQPMNT_NM LIKE '%' || #{eqpSearchWord} || '%'
AND E.EQPMNT_DELYN='N' ORDER BY (CASE WHEN E.TH_NO = 0 THEN 1 ELSE 2 END), E.EQPMNT_NO ASC)A ) B WHERE 1=1 AND B.RNUM >= #{rowCnt} * (#{page} - 1) + 1 AND B.RNUM <= #{rowCnt} * #{page}

mapper.update:
Update
	equipment
set
	eqpmnt_using=#{eqpmntUsing}
WHERE
	eqpmnt_no = #{eqpmntNo}

mapper.insert:

	select seq_eqp_log.nextval from dual

INSERT INTO equipment_log (
    log_no
    , mem_no
    , req_dt
    , use_strt_dt
    , rtn_estmt_dt
    , use_for
    , eqpmnt_no
    , ch_no
    , th_no
) VALUES (
	#{logNo}
	, #{memNo}
	, SYSDATE
	, #{useStrtDt}
	, #{rtnEstmtDt}
	, #{useFor}
	, #{eqpmntNo}
	, #{chNo}
	, #{thNo}
)

mapper.update:
UPDATE 
	EQUIPMENT_LOG
SET
	USE_STRT_DT=#{useStrtDt}
	, RTN_ESTMT_DT=#{rtnEstmtDt}
	, USE_FOR=#{useFor}
where LOG_NO=#{logNo}

mapper.select:
SELECT CH_NO
FROM CHANNEL_THREAD
WHERE TH_NO = #{thNo}
